var fxml = require('./FriendlyXML');
var fs = require('fs');

var filename = "file.xml";

fs.readFile( filename, 'utf8', function( err, data )
{
    fxml.ParseString( data, function( result )
    {
        result.Message.Header.To.val = "Mr.Ramon";
        result.Message.Header.From.val = "Dr.Ramsey";
        delete result.Message.Body.Status;
        result.Message.Body.Error = { "val": "900" };

        console.log( fxml.asXML( result, true ) );
    } );
} );
